import {all, fork, takeLatest, put, call, throttle } from 'redux-saga/effects';
import axios from 'axios';
import {ADD_POST_REQUEST, ADD_POST_SUCCESS, ADD_POST_FAILURE,
  ADD_COMMENT_REQUEST, ADD_COMMENT_SUCCESS, ADD_COMMENT_FAILURE,
  REMOVE_POST_REQUEST, REMOVE_POST_FAILURE, REMOVE_POST_SUCCESS,
  LOAD_POSTS_REQUEST, LOAD_POSTS_FAILURE, LOAD_POSTS_SUCCESS,
  LIKE_POST_REQUEST, LIKE_POST_FAILURE, LIKE_POST_SUCCESS,
  UNLIKE_POST_REQUEST, UNLIKE_POST_FAILURE, UNLIKE_POST_SUCCESS, UPLOAD_IMAGES_REQUEST, UPLOAD_IMAGES_FAILURE, UPLOAD_IMAGES_SUCCESS, RETWEET_REQUEST, RETWEET_SUCCESS, RETWEET_FAILURE, LOAD_POST_REQUEST, LOAD_POST_SUCCESS, LOAD_POST_FAILURE, LOAD_USER_POSTS_REQUEST, LOAD_USER_POSTS_SUCCESS, LOAD_USER_POSTS_FAILURE, LOAD_HASHTAG_POSTS_REQUEST, LOAD_HASHTAG_POSTS_SUCCESS, LOAD_HASHTAG_POSTS_FAILURE, MODIFY_POST_REQUEST, MODIFY_POST_SUCCESS, MODIFY_POST_FAILURE} from '../reducers/post';
import { ADD_POST_TO_ME, REMOVE_POST_OF_ME} from '../reducers/user';

function loadPostsAPI(lastId) {
  return axios.get(`/posts/related?lastId=${lastId || 0}`);
}
function loadUserPostsAPI(data, lastId) {
  return axios.get(`/user/${data}/posts?lastId=${lastId || 0}`);
}
function loadHashtagPostsAPI(data, lastId) {
  return axios.get(`/hashtag/${encodeURIComponent(data)}?lastId=${lastId || 0}`);
}
function loadPostAPI(data) {
  return axios.get(`/post/${data}`);
}
function addPostAPI(data) {
  return axios.post('/post', data);
}
function addCommentAPI(data) {
  return axios.post(`/post/${data.postId}}/comment`, data);
}
function removeCommentAPI(data) {
  return axios.post('/api/post', data);
}
function unlikePostAPI(data) {
  return axios.delete(`/post/${data}}/like`);
}
function likePostAPI(data) {
  return axios.patch(`/post/${data}}/like`);
}
function removePostAPI(data) {
  return axios.delete(`/post/${data}`);
}
function uploadImagesAPI(data) {
  return axios.post('/post/images', data);
}
function retweetAPI(data) {
  return axios.post(`/post/${data}/retweet`);
}
function modifyPostAPI(data) {
  return axios.patch(`/post/${data.postId}`, data);
}

function* addPost(action) {
  try {
    const result = yield call(addPostAPI, action.data);
    yield put({
      type: ADD_POST_SUCCESS,
      data: result.data,
    });
    yield put({
      type: ADD_POST_TO_ME,
      data: result.data.id,
    });
  } catch (error) {
    yield put({
      type: ADD_POST_FAILURE,
      error: error.response.data,
    });
  }
}

