import React from 'react';
import {useDispatch} from 'react-redux';
import axios from 'axios';
import useSWR from 'swr';
import {List, Button, Card} from 'antd';
import PropTypes from 'prop-types';
import { StopOutlined } from '@ant-design/icons';
import { UNFOLLOW_REQUEST, REMOVE_FOLLOWER_REQUEST } from '../reducers/user';

const fetcher = (url) => axios.get(url, {withCredentials: true}).then((result) => result.data);

function FollowList({header, data, onClickMore, loading, limit}) {
  const dispatch = useDispatch();
  const onCancel = (id) => () => {
    if (header === 'Following') {
      dispatch({
        type: UNFOLLOW_REQUEST,
        data: id,
      });
      const {data: followingsData, error: followingError, mutate: mutateFollowing} = useSWR(`http://localhost:3065/user/followings?limit=${limit}`, fetcher);
      mutateFollowing((prev) => prev.filter((d) => d.id !== id));
    } else {
      dispatch({
        type: REMOVE_FOLLOWER_REQUEST,
        data: id,
      });
      const {data: followersData, error: followerError, mutate: mutateFollower} = useSWR(`http://localhost:3065/user/followers?limit=${limit}`, fetcher);
      mutateFollower((prev) => prev.filter((d) => d.id !== id));
    }
  };

  return (
    <List
      style={{marginBottom: 20}}
      grid={{gutter: 4, xs: 2, md: 3}}
      size="small"
      header={<div>{header}</div>}
      loadMore={(
        <div style={{textAlign: 'center', margin: '10px 0'}}>
          <Button onClick={onClickMore} loading={loading}>Load More</Button>
        </div>
      )}
      bordered
      dataSource={data}
      renderItem={(item) => (
        <List.Item style={{marginTop: 20}}>
          <Card actions={[
            <StopOutlined key="stop" onClick={onCancel(item.id)} />]}
          >
            <Card.Meta description={item.nickname} />
          </Card>
        </List.Item>
      )}
    />
  );
}

FollowList.propTypes = {
  header: PropTypes.string.isRequired,
  data: PropTypes.array.isRequired,
  onClickMore: PropTypes.func.isRequired,
  loading: PropTypes.bool.isRequired,
  limit: PropTypes.number.isRequired,
};

export default FollowList;
